<?php
namespace pinoox\command;


use pinoox\component\console;
use pinoox\component\Dir;
use pinoox\component\File;
use pinoox\component\interfaces\CommandInterface;


class appMake extends console implements CommandInterface
{

	/**
	* The console command name.
	*
	* @var string
	*/
	protected $signature = "app:make";

	/**
	* The console command description.
	*
	* @var string
	*/
	protected $description = "create new application.";

	/**
	* The console command Arguments.
	*
	* @var array
	*/
	protected $arguments = [
        ['package_name', true, 'name of package(its better subdomain of your company for example: com_domain_test).'],
	];

	/**
	* The console command Options.
	*
	* @var array
	*/
	protected $options = [
		[ "name" , "n" , "Specify a name for your application that will be displayed in the dock manager." , 'Test - change me' ],
		[ "developer" , "dev" , "In this section, you introduce your name or team as the creator of this application." , 'Pinoox - change me' ],
		[ "author" , "a" , "Code author, for copyright in source code." , 'Pinoox' ],
		[ "link" , "l" , "Author Connect Link, for copyright in source code." , 'https://www.pinoox.com/' ],
		[ "license" , null , "Put your license in source code (for example:`MIT`)." , null ],
		[ "description" , "d" , "Enter a description of how the app works in this section." , 'This is simple application.' ],
		[ "version_name" , "vn" , "You will specify a name for the application version." , '1.0.0' ],
		[ "version_code" , "vc" , "A numeric code for the application version. Important for updating." , 1 ],
		[ "hidden" , null , "Whether the application can be displayed on the Pinoox Manager desktop or not." , false ],
		[ "router" , "r" , "How to receive service from the routing system." , 'multiple' ],
		[ "minpin" , "mp" , "What version of Pinoox is your application compatible with?" , 1 ],
		[ "lnag" , "ln" , "Specify the main language of your application." , 'en' ],
        [ "pinlogo" , null , "if write this,pinoox logo into the in source code." , null ],
        [ "ignoreCopyright" , 'i' , "Don't show any copyright in source." , null ],
//		[ "model" , "m" , "Create simple model." , null],
//		[ "view" , "v" , "Create Theme folder." , null],
//		[ "controller" , "c" , "Create simple controller." , null],
//		[ "mvc" , null , "Create simple model,theme and controller." , null],
	];

    private $appPath = null ;
    private $package = null ;
    private $nameSpace = null ;

    /**
	* Execute the console command.
	*
	*/
	public function handle()
	{
        $this->appPath = Dir::path('~apps/' . $this->argument('package_name').'/');
        if ( is_dir($this->appPath) )
            self::error('Package name is exist!');
        $this->package = $this->argument('package_name');
        $this->nameSpace = 'pinoox\app\\'.$this->argument('package_name').'\\';

        try {
            if ($this->makeDefaultFolders()
                and $this->makeAppFile()
                and $this->makeTheme()
                and $this->makeLang()
                and $this->makeModel()
                and $this->makeController()
                and $this->makeConfig()) {
                self::success(sprintf('`%s` was created successfully in `%s`', $this->package, $this->appPath));
            } else {
                $this->danger('Something got error during make application. please do it manually!');
                $this->newLine();
                File::remove($this->appPath);
                $this->error('Some error happened!');
            }
        } catch (\Exception $exception){
            $this->danger('Something got error during make application. please do it manually!');
            $this->newLine();
            $this->danger($exception->getMessage());
            $this->newLine();
            File::remove($this->appPath);
            $this->error('Some error happened!');
        }
	}

    private function makeDefaultFolders(){
	    return $this->makeFolder('')
	            and $this->makeFolder('controller')
                and $this->makeFolder('lang')
                and $this->makeFolder('lang/fa')
                and $this->makeFolder('lang/en')
                and $this->makeFolder('theme')
                and $this->makeFolder('theme/default')
                and $this->makeFolder('resources')
                and $this->makeFolder('resources')
                and $this->makeFolder('model')
                and $this->makeFolder('config');
    }

    private function makeAppFile()
    {
        $code = "<?php \n";
        $code .= $this->makeCopyWriteCode();
        $code .= "return array(\n";
        $code .= sprintf("\t'package-name' => '%s',\n" , self::argument('package_name'));
        $code .= sprintf("\t'name' => '%s',\n" , self::option('name'));
        $code .= sprintf("\t'developer' => '%s',\n" , self::option('developer'));
        $code .= sprintf("\t'description' => '%s',\n" , self::option('description'));
        $code .= sprintf("\t'version-name' => '%s',\n" , self::option('version_name'));
        $code .= sprintf("\t'version-code' => %d,\n" , intval(self::option('version_code')) );
        $code .= "\t'icon' => 'icon.png',\n";
        $code .= "\t'theme' => 'default',\n";
        $code .= "\t'enable' => true,\n";
        $code .= "\t'hidden' => false,\n";
        $code .= sprintf("\t'router' => '%s',\n" , self::option('router'));
        $code .= sprintf("\t'minpin' => %d,\n" , intval(self::option('minpin')));
        $code .= sprintf("\t'lnag' => '%s',\n" , self::option('lnag'));
        $code .= "\t'service' => [],\n";
        $code .= "\t'loader' => [],\n";
        $code .= ");";
        return $this->makeFile('app.php',$code);
    }
    private function makeConfig()
    {
        $code = "<?php \n";
        $code .= $this->makeCopyWriteCode();
        $code .= "return array(\n";
        $code .= "\t'main_page' => 'hello',\n";
        $code .= ");";
        return $this->makeFile('config/test.config.php',$code);
    }

    private function makeTheme()
    {
        $code = "<!doctype html>\n";
        $code .= "<html lang=\"fa\">\n";
        $code .= "<head>\n";
        $code .= "\t<meta charset=\"UTF-8\">\n";
        $code .= "\t<title><?php lang('welcome.welcome_to_pinoox'); ?></title>\n";
        $code .= "</head>\n";
        $code .= "<body>\n";
        $code .= "\t<table><tr>\n";
        $code .= "\t\t<td><img src=\"<?php echo \$_url; ?>pinoox.png\" alt=\"logo\" style='max-width: 100px;'></td>\n";
        $code .= "\t\t<td><img src=\"<?php echo furl(\"resources/default.png\"); ?>\" style='max-width: 100px;'></td>\n";
        $code .= "\t</tr></table>\n";
        $code .= "\t<h1>Hello World!</h1>\n";
        $code .= "\t<h2><?php lang('welcome.welcome_to_pinoox'); ?></h2>\n";
        $code .= "</body>\n";
        $code .= "</html>\n";
        $image = '';
        $image2 = '';
        return $this->makeFile('theme/default/hello.php',$code)
            and $this->makeFile('theme/default/pinoox.png', base64_decode($image))
            and $this->makeFile('resources/default.png', base64_decode($image2))
            and $this->makeFile('icon.png', base64_decode($image2));
    }

    private function makeLang()
    {
        $code = "<?php \n";
        $code .= $this->makeCopyWriteCode();
        $code .= "return [\n";
        $codeFa = "\t'welcome_to_pinoox' => '   ',\n";
        $codeEn = "\t'welcome_to_pinoox' => 'Welcome to Pinoox',\n";
        $EndCode = "];\n";
        return
            $this->makeFile('lang/fa/welcome.lang.php',$code.$codeFa.$EndCode )
            and $this->makeFile('lang/en/welcome.lang.php',$code.$codeEn.$EndCode );
    }


    private function makeModel()
    {
        $code = "<?php \n";
        $code .= $this->makeCopyWriteCode();
        $code .= $this->makeNameSpace('model');
        $code .= "use pinoox\model\PinooxDatabase;\n\n";
        $code .= "class TestModel extends PinooxDatabase\n";
        $code .= "{\n\n";
        $code .= "\tpublic static function fetch_all()\n";
        $code .= "\t{\n";
        $code .= "\t\treturn [];\n";
        $code .= "\t}\n\n";
        $code .= "}\n";
        return $this->makeFile('model/test.model.php',$code);
    }

    private function makeController()
    {
        $code = "<?php \n";
        $code .= $this->makeCopyWriteCode();
        $code .= $this->makeNameSpace('controller');
        $code .= "use pinoox\component\interfaces\ControllerInterface;\n";
        $code .= "use pinoox\component\Response;\n";
        $code .= "use pinoox\component\Template;\n\n";
        $code .= "class MasterController implements ControllerInterface\n";
        $code .= "{\n\n";
        $code .= "\tprotected static \$template;\n\n";
        $code .= "\tpublic function __construct()\n";
        $code .= "\t{\n";
        $code .= "\t\tself::\$template = new Template();\n";
        $code .= "\t}\n\n";
        $code .= "\tpublic function _main()\n";
        $code .= "\t{\n";
        $code .= "\t\tResponse::redirect(url());\n";
        $code .= "\t}\n\n";
        $code .= "\tpublic function _exception()\n";
        $code .= "\t{\n";
        $code .= "\t\tResponse::redirect(url());\n";
        $code .= "\t}\n\n";
        $code .= "}\n";
        $code2 = "<?php \n";
        $code2 .= $this->makeCopyWriteCode();
        $code2 .= $this->makeNameSpace('controller');
        $code2 .= "use pinoox\component\Config;\n\n";
        $code2 .= "class MainController extends MasterController\n";
        $code2 .= "{\n\n";
        $code2 .= "\tpublic function _main()\n";
        $code2 .= "\t{\n";
        $code2 .= "\t\t\$template_name = Config::get('test.main_page');\n";
        $code2 .= "\t\tself::\$template->show(\$template_name);\n";
        $code2 .= "\t}\n";
        $code2 .= "}\n";
        return
            $this->makeFile('controller/master.controller.php',$code)
            and $this->makeFile('controller/main.controller.php',$code2);
    }

    private function makeFolder($directory){
        return File::make_folder($this->appPath.$directory , false,0777 , false);
    }

    private function makeFile($direction , $content){
        return File::generate($this->appPath.$direction , $content);
    }
    
    private function makeCopyWriteCode(){
        if ( self::hasOption('ignoreCopyright' , $this->options) )
            return "";
        $code ="/**\n";
        if ( self::hasOption('pinlogo' , $this->options) ) {
            $code .= " *      ****  *  *     *  ****  ****  *    *\n";
            $code .= " *      *  *  *  * *   *  *  *  *  *   *  *\n";
            $code .= " *      ****  *  *  *  *  *  *  *  *    *\n";
            $code .= " *      *     *  *   * *  *  *  *  *   *  *\n";
            $code .= " *      *     *  *    **  ****  ****  *    *\n";
        } else {
            $code .= " *\n";
        }
        $code .= " *\n";
        $code .= self::hasOption('author' , $this->options) ? sprintf(" * @author   %s\n" , self::option('author')) : "";
        $code .= self::hasOption('link' , $this->options) ? sprintf(" * @link %s\n" , self::option('link')) : "";
        $code .= self::hasOption('license' , $this->options) ? sprintf(" * @license  %s\n" , self::option('license') == 'MIT' ? 'https://opensource.org/licenses/MIT MIT License' : self::option('license') ) : "";
        $code .=" */\n\n";
        return $code;
    }

    private function makeNameSpace($dire){
        return sprintf("namespace %s%s;\n\n",$this->nameSpace,$dire);
    }


}